<script src="https://cdnjs.cloudflare.com/ajax/libs/Dropify/0.2.2/js/dropify.js" integrity="sha512-hJsxoiLoVRkwHNvA5alz/GVA+eWtVxdQ48iy4sFRQLpDrBPn6BFZeUcW4R4kU+Rj2ljM9wHwekwVtsb0RY/46Q==" crossorigin="anonymous" referrerpolicy="no-referrer"></script>

<script>
    function bindingValue(namee, value) {
        $('textarea[name="' + namee + '"]').val(value);
    }
    $(document).ready(function() {
        $('input[type="radio"]').iCheck({
            radioClass: 'iradio_minimal-blue'
        });

        $('input').on('ifChecked', function() {
            const modal = $('.explained-modal');
            const data_value = $(this).attr('data-value');
            const data_no = $(this).attr('data-no');
            const data_id = $(this).attr('data-id').replace(/\./g, '_');

            modal.empty();

            if ($(this).val() == 'tidak_berlaku') {
                $(".tidak_sesuai_" + data_id).remove();
                $(".observasi_" + data_id).remove();

                const target = $('.tidak_berlaku');
                const targetClass = 'penjelasan_' + data_id;

                let uploader =
                    '<label>Bukti Objektif</label>' +
                    '<input type="file" name="bukti_' + targetClass + '" class="dropify" data-height="100" data-allowed-file-extensions="jpeg png jpg" data-max-file-size-preview="2M">';

                $('#myModal').modal('show');
                let htmlModal = '';
                let html = '<div class="input-group ' + targetClass + '" style="margin-bottom:10px">';
                html += '<p>Kriteria ' + data_no + ' : ';
                html += '<b> "' + data_value + '"</b></p>';
                htmlModal = html;
                htmlModal += '<textarea id="' + targetClass + '" onchange="bindingValue(this.id, this.value)" rows="4" class="textarea form-control" placeholder="Penjelasan"></textarea></div>';
                html += '<textarea name="' + targetClass + '" rows="4" class="textarea form-control" placeholder="Penjelasan"></textarea>' + uploader + '</div>';

                target.append(html);
                modal.append(htmlModal);
            } else if ($(this).val() == 'minor' || $(this).val() == 'major') {
                $(".penjelasan_" + data_id).remove();
                $(".observasi_" + data_id).remove();

                if ($(".tidak_sesuai_" + data_id)) {
                    $(".tidak_sesuai_" + data_id).remove();
                }

                const target = $('.tidak_sesuai');
                const targetClass = 'tidak_sesuai_' + data_id;

                let uploader =
                    '<label>Bukti Objektif</label>' +
                    '<input type="file" name="bukti_' + targetClass + '" class="dropify" data-height="100" data-allowed-file-extensions="jpeg png jpg" data-max-file-size-preview="2M">';

                $('#myModal').modal('show');
                let htmlModal = '';
                let html = '<div class="input-group ' + targetClass + '" style="margin-bottom:10px">';
                html += '<p>Kriteria ' + data_no + ' : ';
                html += '<b> "' + data_value + '"</b></p>';
                htmlModal = html;
                htmlModal += 'Uraian Permasalahan/Ketidaksesuaian : <textarea id="' + targetClass + '" onchange="bindingValue(this.id, this.value)" rows="4" class="textarea form-control" placeholder="Uraian Permasalahan/Ketidaksesuaian"></textarea></div>';
                html += 'Uraian Permasalahan/Ketidaksesuaian :<textarea name="' + targetClass + '" rows="4" class="textarea form-control" placeholder="Uraian Permasalahan/Ketidaksesuaian"></textarea>' + uploader + '</div>';

                target.append(html);
                modal.append(htmlModal);
            } else if ($(this).val() == 'observasi') {
                $(".penjelasan_" + data_id).remove();
                $(".tidak_sesuai_" + data_id).remove();

                const target = $('.observasi');
                const targetClass = 'observasi_' + data_id;

                let uploader =
                    '<label>Bukti Objektif</label>' +
                    '<input type="file" name="bukti_' + targetClass + '" class="dropify" data-height="100" data-allowed-file-extensions="jpeg png jpg" data-max-file-size-preview="2M">';

                $('#myModal').modal('show');
                let htmlModal = '';
                let html = '<div class="input-group ' + targetClass + '" style="margin-bottom:10px">';
                html += '<p>Kriteria ' + data_no + ' : ';
                html += '<b> "' + data_value + '"</b></p>';
                htmlModal = html;
                htmlModal += '<label for="bukti_' + targetClass + '">Bukti objektif :</label><textarea id="bukti_' + targetClass + '" onchange="bindingValue(this.id, this.value)" rows="4" class="textarea form-control" placeholder="Bukti Objektif"></textarea><br>';
                htmlModal += '<label>Catatan :</label><textarea id="' + targetClass + '" onchange="bindingValue(this.id, this.value)" rows="4" class="textarea form-control" placeholder="Catatan"></textarea></div>';
                html += '<label>Bukti objektif :</label><textarea name="bukti_' + targetClass + '" rows="4" class="textarea form-control" placeholder="Bukti Objektif"></textarea><br>';
                html += '<label>Catatan :</label><textarea name="' + targetClass + '" rows="4" class="textarea form-control" placeholder="Catatan"></textarea></div>';

                target.append(html);
                modal.append(htmlModal);

            } else {
                $(".penjelasan_" + data_id).remove();
                $(".tidak_sesuai_" + data_id).remove();
                $(".observasi_" + data_id).remove();
            }


            $('.dropify').dropify();

        });

        $('.btn_save').click(function(e) {
            e.preventDefault();
            $('.message').fadeOut();
            var audit_pemenuhan = $('#audit_pemenuhan');
            var data_post = audit_pemenuhan.serializeArray();
            var save_type = $(this).attr('data-stype');

            let tidak_berlaku = $('#tidak_berlaku').serializeArray();
            let tidak_sesuai = $('#tidak_sesuai').serializeArray();
            let observasi = $('#observasi').serializeArray();

            // let formData = new FormData();

            // formData.append("files", );
            console.log(tidak_berlaku);


            // $.each(tidak_berlaku, function() {
            //     data_post.push({
            //         name: this.name,
            //         value: this.value
            //     });
            // });
            // $.each(tidak_sesuai, function() {
            //     data_post.push({
            //         name: this.name,
            //         value: this.value
            //     });
            // });
            // $.each(observasi, function() {
            //     data_post.push({
            //         name: this.name,
            //         value: this.value
            //     });
            // });

            // $('input[type="radio"]:checked').each(function() {
            //     data_post.push({
            //         name: this.name,
            //         value: this.value
            //     });
            // })
            // data_post.push({
            //     name: 'id_task',
            //     value: <?= $id_task; ?>
            // });
            // data_post.push({
            //     name: 'save_type',
            //     value: save_type
            // });

            // console.log(data_post)

            // $('.loading').show();

            $.ajax({
                    url: BASE_URL + '/administrator/audit_tasks/save_audit_pemenuhan',
                    type: 'POST',
                    dataType: 'json',
                    data: data_post,
                })
                .done(function(res) {

                    if (res.success) {

                        if (save_type == 'back') {
                            window.location.href = res.redirect;
                            return;
                        }

                        $('.message').printMessage({
                            message: res.message
                        });
                        $('.message').fadeIn();
                        resetForm();
                        $('.chosen option').prop('selected', false).trigger('chosen:updated');
                        // ket_kriteria_tdk_berlaku.setData('');


                    } else {
                        if (res.errors) {

                            $.each(res.errors, function(index, val) {
                                $('form #' + index).parents('.form-group').addClass('has-error');
                                $('form #' + index).parents('.form-group').find('small').prepend(`
            <div class="error-input">` + val + `</div>
            `);
                            });
                            $('.steps li').removeClass('error');
                            $('.content section').each(function(index, el) {
                                if ($(this).find('.has-error').length) {
                                    $('.steps li:eq(' + index + ')').addClass('error').find('a').trigger('click');
                                }
                            });
                        }
                        $('.message').printMessage({
                            message: res.message,
                            type: 'warning'
                        });
                    }

                })
                .fail(function() {
                    $('.message').printMessage({
                        message: 'Error save data',
                        type: 'warning'
                    });
                })
                .always(function() {
                    $('.loading').hide();
                    $('html, body').animate({
                        scrollTop: $(document).height()
                    }, 2000);
                });

            return false;
        }); /*end btn save*/
    });
</script>


